import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, Button, Alert, TouchableOpacity } from 'react-native';
import axios from 'axios';
import Update from './Update'; // Import the Update component
import Updatestyle from '../style/Updatestyle';
import Icon from 'react-native-vector-icons/FontAwesome';
import { MenuProvider, Menu, MenuTrigger, MenuOptions, MenuOption } from 'react-native-popup-menu'; // Add this import statement

const Read = ({ navigation }) => {
  const [issues, setIssues] = useState([]);
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  useEffect(() => {
    fetchIssues();
  }, [refreshKey]);

  const fetchIssues = async () => {
    try {
      const response = await axios.get('https://crudcrud.com/api/24c4607bd64f47aea54d99701af5e0eb/read');
      if (response.status === 200) {
        setIssues(response.data);
      } else {
        Alert.alert('Error', 'Failed to fetch issues');
      }
    } catch (error) {
      console.error('Error fetching issues:', error);
      Alert.alert('Error', 'An error occurred');
    }
  };

  const deleteIssue = async (id) => {
    try {
      const response = await axios.delete(`https://crudcrud.com/api/24c4607bd64f47aea54d99701af5e0eb/read/${id}`);
      if (response.status === 200) {
        fetchIssues(); // Refresh the list after deletion
      } else {
        Alert.alert('Error', 'Failed to delete issue');
      }
    } catch (error) {
      console.error('Error deleting issue:', error);
      Alert.alert('Error', 'An error occurred while deleting the issue');
    }
  };

  const handleUpdatePress = (issueId) => {
    setSelectedIssue(issueId);
  };

  const handleCancelUpdate = () => {
    setSelectedIssue(null);
  };

  const handleRefresh = () => {
    setRefreshKey((prevKey) => prevKey + 1);
  };

  return (
    <MenuProvider>
      <View style={Updatestyle.container}>
        <Text style={Updatestyle.heading}>Issues:</Text>
        <FlatList
          data={issues}
          keyExtractor={(item) => item._id}
          renderItem={({ item }) => (
            <View style={Updatestyle.issueContainer}>
              <Text style={Updatestyle.issueText}>{item.text}</Text>
              <Menu>
                <MenuTrigger>
                  <TouchableOpacity>
                    <Icon name="ellipsis-v" size={20} color="black" />
                  </TouchableOpacity>
                </MenuTrigger>
                <MenuOptions>
                  <MenuOption onSelect={() => handleUpdatePress(item._id)} text="Update">
                    <Text style={Updatestyle.menuOption}><Icon name="pencil" size={15} color="black" /></Text>
                  </MenuOption>
                  <MenuOption onSelect={() => deleteIssue(item._id)} text="Delete">
                    <Text style={Updatestyle.menuOption}><Icon name="trash-o" size={15} color="red" /></Text>
                  </MenuOption>
                </MenuOptions>
              </Menu>
            </View>
          )}
        />
        <TouchableOpacity style={Updatestyle.refreshButton} onPress={handleRefresh}>
          <Text style={Updatestyle.buttonText}>Refresh</Text>
        </TouchableOpacity>

        {selectedIssue && (
          <Update
            issueId={selectedIssue}
            refreshRead={handleRefresh}
            onCancel={handleCancelUpdate}
          />
        )}
      </View>
    </MenuProvider>
  );
};

export default Read;
