import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View } from 'react-native';
import Create from './crud/Create';
import Read from './crud/Read';
import { Text } from 'react-native-web';

export default function App() {
  const [refreshKey, setRefreshKey] = useState(0);

  const refreshRead = () => {
    // In a real-world scenario, you would fetch updated data here
    // For demonstration purposes, we'll log a message and increment a state key
    console.log('Refreshing Read component or any other logic');
    setRefreshKey((prevKey) => prevKey + 1);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>IssueLogger</Text>
      <Create refreshRead={refreshRead} />
      <Read refreshKey={refreshKey} />
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F2E3DB',
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
    color: '#263A29',
  },
});
