import React, { useState } from 'react';
import { View, Text, FlatList, TextInput, TouchableOpacity, Alert } from 'react-native';
import axios from 'axios';
import Updatestyle from '../style/Updatestyle';
import Icon from 'react-native-vector-icons/FontAwesome';

const Read = ({ navigation, fetchedData }) => {
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [updatedText, setUpdatedText] = useState('');

  const handleResponse = (response, errorMessage, onSuccess) => {
    if (response.status === 200) {
      onSuccess();
    } else {
      Alert.alert('Error', errorMessage);
    }
  };

  const handleError = (error, errorMessage) => {
    console.error(errorMessage, error);
    Alert.alert('Error', 'An error occurred');
  };

  const deleteIssue = async (id) => {
    try {
      const response = await axios.delete(`https://crudcrud.com/api/f856cf192fc645ae8076683d038c8a57/read/${id}`);
      handleResponse(response, 'Failed to delete issue', fetchIssues);
    } catch (error) {
      handleError(error, 'Error deleting issue');
    }
  };

  const updateIssue = async () => {
    try {
      const response = await axios.put(`https://crudcrud.com/api/f856cf192fc645ae8076683d038c8a57/read/${selectedIssue}`, {
        text: updatedText,
      });
      handleResponse(response, 'Failed to update issue', () => {
        fetchIssues();
        setSelectedIssue(null);
        setUpdatedText('');
      });
    } catch (error) {
      handleError(error, 'Error updating issue');
    }
  };

  const handleUpdatePress = (issueId) => {
    setSelectedIssue(issueId);
    const selectedIssueText = fetchedData.find((issue) => issue._id === issueId)?.text;
    setUpdatedText(selectedIssueText || '');
  };

  const handleCancelUpdate = () => {
    setSelectedIssue(null);
    setUpdatedText('');
  };

  const fetchIssues = async () => {
    try {
      const response = await axios.get('https://crudcrud.com/api/f856cf192fc645ae8076683d038c8a57/read');
      handleResponse(response, 'Failed to fetch issues', () => {});
    } catch (error) {
      handleError(error, 'Error fetching issues');
    }
  };

  return (
    <View style={Updatestyle.container}>
      <Text style={Updatestyle.heading}>Issues:</Text>
      <TouchableOpacity style={Updatestyle.refreshButton} onPress={fetchIssues}>
        <Text style={Updatestyle.buttonText}>Refresh</Text>
      </TouchableOpacity>
      <FlatList
        data={fetchedData}
        keyExtractor={(item) => item._id}
        renderItem={({ item }) => (
          <View style={Updatestyle.issueContainer}>
            <Text style={Updatestyle.issueText}>{item.text}</Text>
            <TouchableOpacity onPress={() => handleUpdatePress(item._id)}>
              <Text style={Updatestyle.button}><Icon name="pencil" size={15} color="black" /></Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => deleteIssue(item._id)}>
              <Text style={Updatestyle.button}><Icon name="trash-o" size={15} color="red" /></Text>
            </TouchableOpacity>
          </View>
        )}
      />
      {selectedIssue !== null && (
        <View style={Updatestyle.updateContainer}>
          <TextInput
            style={Updatestyle.input}
            placeholder="Enter updated text"
            value={updatedText}
            onChangeText={(text) => setUpdatedText(text)}
          />
          <TouchableOpacity style={Updatestyle.refreshButton} onPress={updateIssue}>
            <Text style={Updatestyle.buttonText}>Update</Text>
          </TouchableOpacity>
          <TouchableOpacity style={Updatestyle.refreshButton} onPress={handleCancelUpdate}>
            <Text style={Updatestyle.buttonText}>Cancel</Text>
          </TouchableOpacity>
        </View>
      )}
    </View>
  );
};

export default Read;
