import { StyleSheet } from 'react-native';

export default StyleSheet.create({
  container: {
    backgroundColor: 'white',
    borderRadius: 10,
    width: '90%', // Use percentage-based width
    maxWidth: 400, // Set a maximum width for the container
    alignSelf: 'center', // Center the container horizontally
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    padding: '5%', // Use percentage-based padding
    marginVertical: '5%', // Use percentage-based margin
  },
  heading: {
    fontSize: '5%', // Use percentage-based font size
    fontWeight: 'bold',
    marginBottom: '3%', // Use percentage-based margin
  },
  issueContainer: {
    marginBottom: '5%', // Use percentage-based margin
    borderBottomWidth: 1,
    paddingBottom: '3%', // Use percentage-based padding
    backgroundColor: '#f0f0f0', // Grey background color
    borderRadius: 8, // Optional: Add border-radius for rounded corners
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    flexDirection: 'row', // Arrange children in a row
    justifyContent: 'space-between', // Space between the text and icons
    alignItems: 'center', // Vertically center the content
  },

  // Your existing styles...

  // Style for the icons
  iconContainer: {
    flexDirection: 'row',
    alignItems: 'flex-end',
  },

  issueText: {
    fontSize: '3%', // Use percentage-based font size
    marginBottom: '2%', // Use percentage-based margin
    fontFamily: 'serif',
  },
  button: {
    color: 'blue',
    fontSize: '3%', // Use percentage-based font size
    marginVertical: '2%', // Use percentage-based margin
  },
  refreshButton: {
    backgroundColor: '#3498db',
    padding: '2%', // Use percentage-based padding
    borderRadius: 5,
    marginTop: '3%', // Use percentage-based margin
  },
  buttonText: {
    color: '#fff',
    textAlign: 'center',
    fontSize: '3%', // Use percentage-based font size
  },
  input: {
    height: '8%', // Use percentage-based height
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: '3%', // Use percentage-based margin
    paddingHorizontal: '2%', // Use percentage-based padding
    width: '100%',
    borderRadius: 5,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
});
