import { StyleSheet } from 'react-native';

export default StyleSheet.create({
    container: {
      padding: 20,
      backgroundColor: '#fff',
    },
    heading: {
      fontSize: 20,
      fontWeight: 'bold',
      marginBottom: 10,
    },
    issueContainer: {
      marginBottom: 15,
      borderBottomWidth: 1,
      paddingBottom: 10,
    },
    issueText: {
      fontSize: 16,
      marginBottom: 5,
    },
    button: {
      color: 'blue',
      fontSize: 16,
      marginVertical: 5,
    },
    refreshButton: {
      backgroundColor: 'blue',
      padding: 10,
      borderRadius: 5,
      marginTop: 10,
    },
    buttonText: {
      color: '#fff',
      textAlign: 'center',
      fontSize: 16,
    },
  });
