import { StyleSheet } from 'react-native';

export default StyleSheet.create({
    container: {
      backgroundColor: 'white',
    borderRadius: 10,
    width: '235px',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    padding: 16,
    margin: 16,
    },
    heading: {
      fontSize: 20,
      fontWeight: 'bold',
      marginBottom: 10,
    },
    issueContainer: {
      marginBottom: 15,
    borderBottomWidth: 1,
    paddingBottom: 10,
    backgroundColor: '#f0f0f0', // Grey background color
    borderRadius: 8, // Optional: Add border-radius for rounded corners
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 'auto',
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5, 
    },
    issueText: {
      fontSize: 16,
      marginBottom: 5,
      fontFamily: 'serif'
    },
    button: {
      color: 'blue',
      fontSize: 16,
      marginVertical: 5,
    },
    refreshButton: {
      backgroundColor: '#3498db',
      padding: 10,
      borderRadius: 5,
      marginTop: 10,
    },
    buttonText: {
      color: '#fff',
      textAlign: 'center',
      fontSize: 16,
    },
  });
