import { StyleSheet } from 'react-native';

export default StyleSheet.create({
  container: {
    background: 'blue',
    color: 'white',
    padding: '5em',
    borderRadius: '50px',
    width: '300px',
    maxWidth: '300px',
    margin: '2em',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  input: {
    borderRadius: '50px',
    background: '#7c7c7c2b',
    color: 'white',
    fontFamily: 'inherit',
    fontSize: '130%',
    border: '2px solid black', 

  },
  button: {
    margin: 8,  // Converted from 1em to 8 in React Native
    borderRadius: '30%',  // Border radius is not directly supported in React Native, consider using width/height to achieve similar results
    borderWidth: 0,  // Border is set to none
    height: 46,  // Height value in React Native (not converted from em)
    width: 55,   // Width value in React Native (not converted from em)
    backgroundColor: '#7c7c7c2b',
    color: 'burlywood',
    cursor: 'pointer',
  },
  buttonText: {
    color: '#fff',
    textAlign: 'center',
    fontWeight: 'bold',
  },
});
