import React, { useState } from 'react';
import { View, TextInput, Button, Alert } from 'react-native';

const Read = () => {
  const [text, setText] = useState('');

  const handlePostText = async () => {
    try {
      const apiUrl = 'https://crudcrud.com/api/9ac036badf0e4c6c9271c7bbd52e3ad1/creating';
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text }),
      });

      if (response.ok) {
        // Successful post
        Alert.alert('Success', 'Text posted successfully');
      } else {
        // Handle errors
        Alert.alert('Error', 'Failed to post text');
      }
    } catch (error) {
      console.error('Error:', error);
      Alert.alert('Error', 'An unexpected error occurred');
    }
  };

  return (
    <View>
      <TextInput
        placeholder="Enter text"
        value={text}
        onChangeText={(newText) => setText(newText)}
      />
      <Button title="Post Text" onPress={handlePostText} />
    </View>
  );
};

export default Read;
