import React, { useState } from 'react';
import { View, TextInput, TouchableOpacity, Text } from 'react-native';
import axios from 'axios';
import Createstyle from '../style/Createstyle';

const Create = ({ navigation, refreshRead }) => {
  const [issueText, setIssueText] = useState('');

  const createIssue = async () => {
    try {
      const response = await axios.post('https://crudcrud.com/api/f856cf192fc645ae8076683d038c8a57/read', {
        text: issueText,
      });

      if (response.status === 201) {
        refreshRead();
        // Fetch issues after successfully creating an issue
        fetchIssues();

        if (navigation && navigation.goBack) {
          navigation.goBack();
        } else {
          console.error('Navigation object or goBack method is undefined');
        }
      } else {
        console.error('Failed to create issue. Status:', response.status);
      }
    } catch (error) {
      console.error('Error creating issue:', error.message);
    }
  };

  const fetchIssues = async () => {
    // Implement the fetchIssues logic similar to the one in the Read component
    try {
      const response = await axios.get('https://crudcrud.com/api/f856cf192fc645ae8076683d038c8a57/read');
      if (response.status === 200) {
        // Handle the fetched issues data (e.g., update state)
        console.log('Fetched issues:', response.data);
      } else {
        console.error('Failed to fetch issues');
      }
    } catch (error) {
      console.error('Error fetching issues:', error);
    }
  };

  return (
    <View style={Createstyle.container}>
      <TextInput
        style={Createstyle.input}
        placeholder="Create Issue"
        value={issueText}
        onChangeText={(text) => setIssueText(text)}
      />
      <TouchableOpacity style={Createstyle.button} onPress={createIssue}>
        <Text style={Createstyle.buttonText}>Submit</Text>
      </TouchableOpacity>
    </View>
  );
};

export default Create;
